mapper.insert:
	INSERT INTO diaries
	(
    	diary_id
    	,  user_id
    )
    VALUES
    (
    	#{diaryId}
    	,  #{userId}
    )

	INSERT INTO contents
	(
    	diary_id
    	,  diary_title
    	,  diary_content
    	,  created_at
    )
    VALUES
    (
    	#{diaryId}
    	,  #{diaryTitle}
    	,  #{diaryContent}
    	,  CURRENT_TIMESTAMP
    )
