mapper.update:
	UPDATE users
	SET
		username = #{editUsersRequest.username}
    	, age = #{editUsersRequest.age}
    	, email = #{editUsersRequest.email}
    	, password = #{editUsersRequest.password}
    	, auth = #{editUsersRequest.auth}
    	, updated_at = CURRENT_TIMESTAMP
    WHERE
    	user_id = #{userId}
